// Fonction utilitaire : filtrer selon le type sélectionné
function getCurrentFilter() {
    const select = document.getElementById('typeFilter');
    return select ? select.value : 'all';
}

function matchesFilter(item, filterValue) {
    if (!filterValue || filterValue === 'all') {
        return true;
    }

    const type = (item.type || '').toLowerCase();

    switch (filterValue) {
        case 'words':
            return type.includes('nom');
        case 'verbs':
            return type.includes('verbe');
        case 'adjectives':
            return type.includes('adjectif');
        case 'expressions':
            return type.includes('expression');
        case 'sacres':
            return type.includes('juron') || type.includes('sacre') || type.includes('insulte');
        default:
            return true;
    }
}

// Fonction de recherche
function hideSuggestions() {
    const suggestionsEl = document.getElementById('suggestions');
    if (!suggestionsEl) {
        return;
    }
    suggestionsEl.innerHTML = '';
    suggestionsEl.classList.add('hidden');
}

// Mettre à jour les suggestions pendant la frappe
function updateSuggestions() {
    const inputEl = document.getElementById('searchInput');
    const suggestionsEl = document.getElementById('suggestions');

    if (!inputEl || !suggestionsEl) {
        return;
    }

    const query = inputEl.value.trim().toLowerCase();
    const filterValue = getCurrentFilter();

    if (query === '') {
        hideSuggestions();
        return;
    }

    const maxSuggestions = 8;
    const matches = dictionnaire
        .filter(item => item.mot.toLowerCase().includes(query) && matchesFilter(item, filterValue))
        .slice(0, maxSuggestions);

    if (matches.length === 0) {
        hideSuggestions();
        return;
    }

    suggestionsEl.innerHTML = '';

    matches.forEach(item => {
        const li = document.createElement('li');
        li.textContent = item.mot;
        li.className = 'suggestion-item';
        li.addEventListener('click', function () {
            document.getElementById('searchInput').value = item.mot;
            hideSuggestions();
            search();
        });

        suggestionsEl.appendChild(li);
    });

    suggestionsEl.classList.remove('hidden');
}

function search() {
    const input = document.getElementById('searchInput').value.trim().toLowerCase();
    const resultsDiv = document.getElementById('results');
    const noResultsDiv = document.getElementById('noResults');
    const filterValue = getCurrentFilter();

    hideSuggestions();

    if (input === '') {
        resultsDiv.classList.add('hidden');
        noResultsDiv.classList.add('hidden');
        return;
    }

    const result = dictionnaire.find(item =>
        matchesFilter(item, filterValue) && (
            item.mot.toLowerCase() === input ||
            item.mot.toLowerCase().includes(input)
        )
    );

    if (result) {
        displayResult(result);
        resultsDiv.classList.remove('hidden');
        noResultsDiv.classList.add('hidden');
    } else {
        document.getElementById('searchTerm').textContent = input;
        resultsDiv.classList.add('hidden');
        noResultsDiv.classList.remove('hidden');
    }
}

// Afficher le résultat
function displayResult(result) {
    document.getElementById('resultWord').textContent = result.mot;
    document.getElementById('resultType').textContent = result.type;
    document.getElementById('resultDefinition').textContent = result.definition;
    document.getElementById('resultMeaning').textContent = result.signification;
    document.getElementById('resultContext').textContent = result.contexte;
    document.getElementById('resultExample').textContent = result.exemple;
}

// Gérer la touche Enter
function handleKeyPress(event) {
    if (event.key === 'Enter') {
        search();
    }
}

// Mot au hasard
function randomWord() {
    const filterValue = getCurrentFilter();
    let pool = dictionnaire.filter(item => matchesFilter(item, filterValue));
    if (pool.length === 0) {
        pool = dictionnaire;
    }

    const randomIndex = Math.floor(Math.random() * pool.length);
    const randomResult = pool[randomIndex];
    document.getElementById('searchInput').value = randomResult.mot;
    displayResult(randomResult);
    document.getElementById('results').classList.remove('hidden');
    document.getElementById('noResults').classList.add('hidden');
}

// Retour à l'accueil
function goHome() {
    document.getElementById('searchInput').value = '';
    document.getElementById('results').classList.add('hidden');
    document.getElementById('noResults').classList.add('hidden');
}

// Fermer les suggestions quand on clique à l'extérieur
document.addEventListener('click', function (event) {
    const suggestionsEl = document.getElementById('suggestions');
    const searchInput = document.getElementById('searchInput');

    if (!suggestionsEl || !searchInput) {
        return;
    }

    if (suggestionsEl.contains(event.target) || event.target === searchInput) {
        return;
    }

    hideSuggestions();
});
